  const onToken = token => {
    console.log(token)
  }
  return (
    <StripeCheckout
      lable='Pay Now'
      name='CRWN Clothing Ltd'
      billingAddress
      shippingAddress
      image='https://svgshare.com/i/CUz.svg'
      description={`Your total is ${price}`}
      amount={priceForStripe}
      panelLabel='Pay Now'
      token={onToken}
      stripeKey={PublishableKey}
    />
  )
}


export default StripeCheckoutButton
