import React from 'react'

import './category.styles.scss'

import {selectCollections} from '../../reducers/shop/shop.selectors'
import {connect} from "react-redux";
import CollectionItem from "../../components/collection-item/collection-item";

const CollectionPage = ({collection}) => {
  const {title, items} = collection
  return (
    <div className='collection-page'>
      <h2 className='title'>{title}</h2>
      <div className='items'>{items.map(item => (<CollectionItem key={item.id} item={item}/>))}</div>
    </div>
  )
}

const mapStateToProps = (state, ownProps) => ({
  collection: selectCollections(ownProps.match.param.collectionId)(state)
})

export default connect(mapStateToProps)(CollectionPage)
